import React, { Component } from 'react';

export default class Question extends Component {
  checkAnswer = ({ target }) => {
    if (target.name === 'correct-answer') {
      console.log('Você acertou, parabéns!!');
    } else {
      console.log('Que burrro, da zero pra ele');
    }
  };

  render() {
    const { questions } = this.props;
    return (
      <div>
        <h3>
          { questions.length > 0 && questions[0].category }
        </h3>
        <p>
          { questions.length > 0 && questions[0].question }
        </p>
        { questions.length > 0 && questions[0].incorrect_answers.map((answer) => {
          <button
            type="button"
            name="incorrect-answer"
            onClick={ this.checkAnswer }
          >
            { answer }
          </button>;
        }) }
        <button
          type="button"
          name="correct-answer"
          onClick={ this.checkAnswer }
        >
          { questions.length > 0 && questions[0].correct_answer }
        </button>
      </div>
    );
  }
}
